  `erstellt_am` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP COMMENT 'Zeitstempel des Erstellungsdatums',
  `aktualisiert_von` varchar(256) COLLATE utf8mb4_unicode_ci DEFAULT 'DATABASE' COMMENT 'Nutzer welcher diesen EIntrag zuletzt aktualisiert hat',
  `aktualisiert_am` timestamp NULL DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP COMMENT 'Zeitstempel des letzten Aktualisierungsdatums',
