  `gruendungsjahr` int DEFAULT NULL COMMENT 'Gründungsjahr des Herstellers',
  `aktiv` char(1) COLLATE utf8mb4_unicode_ci NOT NULL COMMENT 'Aktivität des Herstellers. (J = Ja, N = Nein)',
  `logo` blob COMMENT 'Logo des Herstellers',
  `logo_mime_type` varchar(16) COLLATE utf8mb4_unicode_ci DEFAULT NULL COMMENT 'Mime type des Logos des Herstellers. Erlaubt sind JPG und PNG',
