  CONSTRAINT `data_motorrad_hersteller_laender_fk` FOREIGN KEY (`herkunftsland_id`) REFERENCES `cmn_laender` (`land_id`) ON DELETE CASCADE ON UPDATE CASCADE
) ENGINE=InnoDB AUTO_INCREMENT=16 DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_unicode_ci COMMENT='Tabelle zum speichern von Daten über Motorrad Hersteller.';
